pragma solidity >=0.5.0 <0.6.0;

import "./Ownable.sol";
import "./IERC20.sol";

contract UprtclAccounts is Ownable {

    mapping(address => mapping(address => bool)) public accounts;

    IERC20 public token;

    function withdraw (address to, uint256 amount) public onlyOwner {
        token.transfer(to, amount);
    }

    function consume (address by, address fromAccount, uint256 amount) public {
        require(accounts[fromAccount][by] == true, "user is not an account usufructuary");
        require(token.allowance(fromAccount, address(this)) >= amount, "not enough funds");

        token.transferFrom(address(this), address(this), amount);
    }

}
